                objectexercise["choose"] = v.choose;
                objectexercise["whenchoose"] = v.whenchoose;
                objectexercise["minute"] = v.minute;
                setExerciseResultArray((prev) => [...prev, objectexercise]);
              });
            } //if exericse state만들기
          }
