                    <Link
                      style={{ textDecoration: "none" }}
                      href={`../money/create?choose=2`}
                    >
                      <Button variant="outline-success" size="sm">
                        지출작성
                      </Button>
                    </Link>
                  </React.Fragment>
                )}
              </ListGroup.Item>

              <ListGroup.Item style={{ textAlign: "left" }}>
                {selectMonth}월수입합계&nbsp;
                {moneySum.monthsumincome}
              </ListGroup.Item>

              <ListGroup.Item style={{ textAlign: "left" }}>
                {selectMonth}월지출합계&nbsp;
                {moneySum.monthsumexpense}
              </ListGroup.Item>
            </ListGroup>
            <Card.Body>
              <Card.Link
                style={{ textDecoration: "none" }}
                href={`../money/modify/${moneyId}`}
              >
                <Button variant="outline-warning" size="sm">
                  수정/삭제
                </Button>
              </Card.Link>
              <Card.Link
                style={{ textDecoration: "none" }}
                href={`../money/list`}
              >
                <Button variant="outline-primary" size="sm">
                  가계부달력이동
                </Button>
              </Card.Link>
            </Card.Body>
          </Card>
        )}
        &nbsp; &nbsp;
        {Array.isArray(exerciseResultArray) &&
        exerciseResultArray.length !== 0 ? (
          <Card style={{ width: "18rem", display: "inline-block" }}>
            <Card.Body>
              <Card.Title>운동</Card.Title>
              {/* <Card.Text>운동</Card.Text> */}
            </Card.Body>
            <ListGroup className="list-group-flush">
              {exerciseResultArray.map((e, i) =>
                e.whenchoose === "1" ? (
                  <React.Fragment key={i}>
                    <ListGroup.Item>아침</ListGroup.Item>
                    <ListGroup.Item>
                      {chooseReturn(e.choose)}/{e.minute}(분)
                      <br />
                      {e.content}
                    </ListGroup.Item>
                  </React.Fragment>
                ) : (
                  <></>
                )
              )}
              {exerciseResultArray.map((e, i) =>
                e.whenchoose === "2" ? (
                  <React.Fragment key={i}>
                    <ListGroup.Item>점심</ListGroup.Item>
                    <ListGroup.Item>
                      {chooseReturn(e.choose)}/{e.minute}(분)
                      <br />
                      {e.content}
                    </ListGroup.Item>
                  </React.Fragment>
                ) : (
                  <></>
                )
              )}
              {exerciseResultArray.map((e, i) =>
                e.whenchoose === "3" ? (
                  <React.Fragment key={i}>
                    <ListGroup.Item>저녁</ListGroup.Item>
                    <ListGroup.Item>
                      {chooseReturn(e.choose)}/{e.minute}(분)
                      <br />
                      {e.content}
                    </ListGroup.Item>
                  </React.Fragment>
                ) : (
                  <></>
                )
              )}
            </ListGroup>
            <Card.Body>
              <Card.Link
                style={{ textDecoration: "none" }}
                href={`../exercise/${findDate}`}
              >
                <Button variant="outline-warning" size="sm">
                  수정/삭제
                </Button>
              </Card.Link>
              <Card.Link
                style={{ textDecoration: "none" }}
                href={`../exercise/list`}
              >
                <Button variant="outline-primary" size="sm">
                  운동달력이동
                </Button>
              </Card.Link>
            </Card.Body>
          </Card>
        ) : (
          <Card style={{ width: "18rem", display: "inline-block" }}>
            <Card.Body>
              <Card.Title>운동</Card.Title>
            </Card.Body>

            <ListGroup className="list-group-flush">
              <ListGroup.Item>
                오늘 운동을 아직 작성하지 않았어요! <br />
                <Link
                  style={{ textDecoration: "none" }}
                  href={`../exercise/create/${findDate}`}
                >
                  {" "}
                  <Button size="sm" variant="outline-primary">
                    운동작성{" "}
                  </Button>
                </Link>
              </ListGroup.Item>
            </ListGroup>
          </Card>
        )}
        <br />
      </Container>
      <br />
      <br />
      <br />
    </>
  );
};
export default MainComponent;
