import React, { useCallback, useEffect, useState } from "react";
import { Link, Navigate, useNavigate, useSearchParams } from "react-router-dom";
import "../layout/stylesCalendar.css";
import Image from "react-bootstrap/Image";
import useCustomMove from "../../hooks/useCustomMove";
import Card from "react-bootstrap/Card";
import useCustomDiary from "../../hooks/useCustomDiary";
import useCustomLogin from "../../hooks/useCustomLogin";
import { getDiaryList } from "../../api/diaryApi";

const makeCalendar = (d) => {
  let date = new Date(d);
  const currentYear = new Date(date).getFullYear(); //예)Mon Apr 01 2024 10:27:04 GMT+0900 (한국 표준시)
  const currentMonth = new Date(date).getMonth() + 1;
  const firstDay = new Date(date.setDate(1)).getDay(); //1 앞에0안붙는다

  //현재 달력첫째날
  let calfirst = "";
  function getCalfirst() {
    calfirst += `${currentYear}-`;

    if (currentMonth < 10) {
      calfirst += `0${currentMonth}`;
    } else {
      calfirst += `${currentMonth}`;
    }
    //calfirst += `01`
    return calfirst;
  } //calfirst 현재달력첫날

  const lastDay = new Date(currentYear, currentMonth, 0).getDate();
  //console.log("lastDay값 ->"+lastDay)//30
  const limitDay = firstDay + lastDay;
  //console.log("limitDay값 ->"+limitDay)//31
  const nextDay = Math.ceil(limitDay / 7) * 7;
  //console.log("nextDay값 ->"+nextDay)//35 35-31=4칸 빈칸만들어진다.

  //   for (let i = 0; i < firstDay; i++) {
  //     <div class="calnoColor"></div>;
  // }//for

  //   for (let i = 1; i <= lastDay; i++) {
  //     <CalendarItem/>;
  //   }
  //   if (nextDay !== 0) {
  //     for (let i = limitDay; i < nextDay; i++) {
  //       <div className="calnoColor"></div>
  //     }
  //   } else {
  //   }
  return { firstDay, lastDay, limitDay, nextDay };
};

const CalendarItem = (props) => {
  //UserId, content ,createdAt ,id , picture
  const { diary, dateobject } = props.i; //api서버후 db에서 받은값
  // console.log("props.i ", props.i.diary[0]);
  return diary.map((i) => (
    <React.Fragment key={i.id}>
      {i.id ? (
        i.picture ? (
          <>
            <Link to={`../read/${i.id}`}>
              👌
              <Image
                src={`http://localhost:8001/img/${i.picture}`}
                rounded
                variant="top"
                style={{
                  display: "block",
                  margin: "0 auto",
                  maxWidth: "50px",
                  maxHeight: "50px",
                  objectFit: "contain",
                }}
              />
            </Link>
          </>
        ) : (
          <Link to={`../read/${i.id}`}>👌</Link>
        )
      ) : (
        <></>
      )}
    </React.Fragment>
  ));
};

const DiaryListComponent = () => {
  const [searchParams] = useSearchParams();
  const { moveToCreate } = useCustomMove();
  const { exceptionHandle } = useCustomLogin();
  const [diary, setDiary] = useState([]);

  useEffect(() => {
    getDiaryList()
      .then((result) => {
        //console.log("DiaryListComponet.js then()진입 ", result);
        setDiary(result);
      })
      .catch((err) => {
        console.log("DiaryListComponet.js catch()진입 ", err);
        exceptionHandle(err);
      });
  }, []);

  const navigate = useNavigate();

  // let date = searchParams.get("yearmonth")
  //   ? searchParams.get("yearmonth")
  //   : props.today;

  // let date = props.today;
  let date = new Date();

  const { firstDay, nextDay, limitDay, lastDay } = makeCalendar(date);

  // console.log("date -> ", date);
  // console.log("firstDay -> ", firstDay);
  // console.log("nextDay -> ", nextDay);
  // console.log("limitDay -> ", limitDay);
  // console.log("lastDay -> ", lastDay);

  let dateItem = [];

  for (let i = 1, j = 0; i <= lastDay; i++, j++) {
    let currentYear = new Date(date).getFullYear();
    let currentMonth = new Date(date).getMonth() + 1;
    currentMonth = currentMonth < 10 ? "0" + currentMonth : currentMonth;
    let ichange = i < 10 ? "0" + i : i;
    let dateobject = `${currentYear}-${currentMonth}-${ichange}`;
    dateItem[j] = {
      dateitem: i,
      dateobject: dateobject,
      diary: diary.filter((a) => a.dateobject === dateobject),
    };
  }
  console.log(
    "diary내용출력 => ",
    dateItem[0].diary,
    "두번째",
    dateItem[7].diary
  );

  //달력에서 이전빈칸
  const beforeDateItem = firstDay - 0;
  let beforeDateItemArray = [];

  if (beforeDateItem !== 0) {
    for (let i = 1, j = 0; i <= beforeDateItem; i++, j++) {
      beforeDateItemArray[j] = i;
    }
  }
  // console.log("beforeDateItem -> ", beforeDateItem);

  //달력에서 이후빈칸
  const afterDateItem = nextDay - limitDay;
  let afterDateItemArray = [];

  if (afterDateItem !== 0) {
    for (let i = 1, j = 0; i <= afterDateItem; i++, j++) {
      afterDateItemArray[j] = i;
    }
  }
  // console.log("afterDateItem -> ", afterDateItem);

  // const [dateItem, setDateItem] = useState([1, ...lastDay]);
  // const [beforeDateItem, setBeforeDateItem] = useState(firstDay - 0);
  // const [afterDateItem, setAfterDateItem] = useState(nextDay - limitDay);

  // 이전달 이동
  const BeforeMonthMove = useCallback(
    (date) => {
      let beforechangeDate = new Date(date.setMonth(date.getMonth() - 1));
      let beforecurrentYear = new Date(beforechangeDate).getFullYear();
      let beforechangeMonth = new Date(beforechangeDate).getMonth() + 1;
      beforechangeMonth =
        beforechangeMonth < 10 ? "0" + beforechangeMonth : beforechangeMonth;
      let resultbeforeMonth =
        beforecurrentYear + "-" + beforechangeMonth + "-01";

      //navigate(`?yearmonth=${resultbeforeMonth}`);

      makeCalendar(new Date(date.setMonth(date.getMonth() - 1)));
    },
    [date]
    //[]
  );

  //다음달이동
  const NextMonthMove = useCallback(
    (date) => {
      let afterchangeDate = new Date(date.setMonth(date.getMonth() + 1));
      let aftercurrentYear = new Date(afterchangeDate).getFullYear();
      let afterchangeMonth = new Date(afterchangeDate).getMonth() + 1;
      afterchangeMonth =
        afterchangeMonth < 10 ? "0" + afterchangeMonth : afterchangeMonth;
      let resultafterMonth = aftercurrentYear + "-" + afterchangeMonth + "-01";

      // navigate(`/?yearmonth=${resultafterMonth}`);

      makeCalendar(new Date(date.setMonth(date.getMonth() + 1)));
    },
    [date]
    //[]
  );

  return (
    <div className="container">
      {" "}
      <section className="py-5">
        <div className="calrap" style={{ margin: "0 auto" }}>
          <div className="calheader">
            <div className="calbtn calprevDay">
              <button onClick={BeforeMonthMove(date)}>이전</button>
              {/* <button>이전</button> */}
            </div>
            <h2 className="caldateTitle">2025년 2월</h2>
            <div className="calbtn calnextDay">
              <button onClick={NextMonthMove(date)}>이후</button>
              {/* <button>이후</button> */}
            </div>
          </div>

          <div className="calgrid caldateHead">
            <div key="{0}">일</div>
            <div key="{1}">월</div>
            <div key="{2}">화</div>
            <div key="{3}">수</div>
            <div key="{4}">목</div>
            <div key="{5}">금</div>
            <div key="{6}">토</div>
          </div>

          <div className="calgrid caldateBoard">
            {beforeDateItemArray.map((i, index) => (
              <div key={index}></div>
            ))}

            {dateItem.map((i, index) => (
              <div>
                {/* <Button onclick={moveToCreate} >{i} </Button> */}
                {i.diary.length === 0 ? (
                  <Link
                    to={`../create/${i.dateobject}`}
                    style={{ textDecoration: "none", color: "inherit" }}
                  >
                    {i.dateitem}
                  </Link>
                ) : (
                  <>{i.dateitem}</>
                )}

                <CalendarItem key={index} i={i} />
              </div>
            ))}
            {afterDateItemArray.map((i, index) => (
              <div key={index}></div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default DiaryListComponent;
