  const [modalVisible, setModalVisible] = useState(false);
  //채팅
  const [msg, setMsg] = useState("");
  const [msgList, setMsgList] = useState([]);
  const [chatroomname, setChatroomname] = useState("");
  const [chatroompassword, setChatroompassword] = useState("");

  const refChatRoom = useRef();
  const refChatRoompassword = useRef();

  const refModalBody = useRef();
  //소켓
  const [socket, setSocket] = useState(null);

  const handleMsg = (e) => {
    //console.log("handleMsg 이벤트핸들러 진입 ->", e.target.value);
    setMsg(() => e.target.value);
  };

  const handleChatroomnameInput = (e) => {
    //console.log("handleChatroomnameInput 이벤트핸들러 진입 ->", e.target.value);
    setChatroomname(() => e.target.value);
  };

  const handleChatroompasswordInput = (e) => {
    //console.log("handleChatroompasswordInput 이벤트핸들러 진입 ->", e.target.value);
    setChatroompassword(() => e.target.value);
  };

  const chatStartClick = (e) => {
    console.log("chatStartClick 이벤트핸들러함수 진입 ");

    e.preventDefault();
    if (!isLogin) {
      alert("로그인 후 이용가능합니다.");
      return;
    }

    if (chatroomname === "" || chatroomname === undefined) {
      alert("방 이름을 입력하세요!");
      return;
    }
    if (chatroompassword === "" || chatroompassword === undefined) {
      alert("방 비밀번호를 입력하세요!");
      return;
    }

    chatfinddb({ chatroomname, chatroompassword }).then((result) => {
      console.log(
        "chatStartClick() 진입후 먼저 chatfinddb() 함수 axios 실행후 결과 -> ",
        result
      );

      if (result === "noexist") {
        console.log("chatStartClick 새로 방 만듬 진입");

        const socket = io("http://localhost:8001", {
          autoConnect: false,
          // query: {
          //   username: loginState, //email
          //   chatroomname: e.target.chatroomname.value,
          //   chatroompassword: e.target.chatroompassword.value,
          // },
        });
        socket.connect();

        setSocket(() => socket);
        // setChatroomname(() => e.target.chatroomname.value);
        // setChatroompassword(() => e.target.chatroompassword.value);
        console.log(
          "chatStartClick 새로 방 만듬 진입 socket.id -> ",
          socket.id
        );

        console.log("chatStartClick 새로 방 만듬 진입 setSocket -> ", socket);
        const sendData = {
          username: loginState, //email
          chatroomname,
          chatroompassword,
        };
        socket.emit("roomcreate", sendData);

        console.log(
          "chatStartClick 새로 방 만듬 진입 roomcreate 이벤트 받기 전 "
        );

        socket.on("roomcreate", roomcreateCallback);

        function roomcreateCallback(msg) {
          console.log(
            "roomcreateCallback 소켓 roomcreate 이벤트 콜백함수 진입-> ",
            msg
          );
          setMsgList((prev) => [
            ...prev,
            {
              msg: msg,
              type: "welcome",
              id: "",
            },
          ]);
          if (msg) {
            setModalVisible(() => true);
            refModalBody.current?.scrollIntoView({
              behavior: "smooth",
              block: "end",
            });
          } //if
          socket.off("roomcreate");
        } //roomcreateCallback
        chatcreatedb({ chatroomname, chatroompassword, loginState });
      } else {
        //chatfinddb then() 내에있다.
        console.log("chatStartClick 기존존재하는 방 진입");

        const socket = io("http://localhost:8001", {
          autoConnect: false,
          // query: {
          //   username: loginState, //email
          //   chatroomname: e.target.chatroomname.value,
          //   chatroompassword: e.target.chatroompassword.value,
          // },
        });
        socket.connect();
        setSocket(() => socket);
        console.log(
          "chatStartClick 기존존재하는 방 진입 socket.id ->",
          socket.id
        );
        console.log("chatStartClick 기존존재하는 방 진입 setSocket->", socket);
        // setChatroomname(() => e.target.chatroomname.value);
        // setChatroompassword(() => e.target.chatroompassword.value);

        const sendData = {
          chatroompassword,
          id: loginState,
          chatroomname,
        };
        socket.emit("friend", sendData);
        console.log(
          "chatStartClick 기존존재하는 방 진입 friend 이벤트 받기전 "
        );

        socket.on("friend", friendCallback);

        function friendCallback(msg) {
          console.log("chatInvitation 소켓 friend 이벤트 받은 결과 -> ", msg);

          setMsgList((prev) => [
            ...prev,
            {
              msg: msg,
              type: "welcome",
              id: "",
            },
          ]);
          socket.off("friend");
        }

        refModalBody.current?.scrollIntoView({
          behavior: "smooth",
          block: "end",
        });

        setModalVisible(() => true);
      } //if-else문
    }); //chatfinddb
  }; //chatStartClick

  //채팅방 db저장
  const chatcreatedb = async ({
    chatroomname,
    chatroompassword,
    loginState,
  }) => {
    await chatAdd({ chatroomname, chatroompassword, loginState })
      .then((result) => {
        console.log("채팅방 chatcreatedb() db생성시 then() 진입 -> ", result);
      })
      .catch((err) => {
        console.log("채팅방 chatcreatedb()  db생성시 에러발생 catch() ", err);
      });
  };

  //채팅방 db저장
  const chatfinddb = async ({ chatroomname, chatroompassword }) => {
    let findresult = null;
    await chatGetPost({ chatroomname, chatroompassword })
      .then((result) => {
        console.log("채팅방 chatfinddb() 찾기 then() 진입 -> ", result);
        if (result.message === "success") {
          findresult = result.room.id;
        } else {
          findresult = "noexist";
        }
      })
      .catch((err) => {
        console.log("채팅방 chatfinddb() 찾기 에러발생 catch() ", err);
        alert("채팅방 생성이 되지 않았습니다. 다시 시도해주세요!");
      });
    return findresult;
  };

  const handleSubmitChat = (e) => {
    e.preventDefault();

    console.log("handleSubmitChat() 진입 ");
    const sendData = {
      //message: e.target.chatcontents.value,
      message: msg,
      username: loginState,
      chatroomname,
    };
    console.log("handleSubmitChat() 진입 message 이벤트보내기 전 ");

    socket.emit("message", sendData);
    console.log("handleSubmitChat() 진입 message 이벤트받기 전 ");

    socket.on("message", messageCallback);

    function messageCallback(msg) {
      console.log("handleSubmitChat() 진입 message 콜백함수 진입 msg-> ", msg);

      const { message, username, chatroomname } = msg;
      console.log("이전 메시지확인 msgList -> ", msgList);

      setMsgList((prev) => [
        ...prev,
        {
          msg: message,
          type: loginState === username ? "me" : "other",
          id: username,
        },
      ]);

      socket.off("message");
    }
    console.log("이후 메시지확인 msgList -> ", msgList);

    refModalBody.current?.scrollIntoView({
      behavior: "smooth",
      block: "end",
    });

    setMsg(() => "");
  };

  const chatCloseModal = () => {
    console.log("chatCloseModal() 진입");
    if (window.confirm("채팅방에서 나가시겠습니까?")) {
      console.log("chatCloseModal onClick 내 out 소켓이벤트 전달전 ");

      socket.emit("out", { chatroomname, loginState, chatroompassword });

      console.log("chatCloseModal onClick 내 out 소켓이벤트 받기전 ");

      socket.on("out", goodbyeCallback);
      function goodbyeCallback(msg) {
        setMsgList((prev) => [
          ...prev,
          {
            msg: msg,
            type: "goodbye",
            id: "",
          },
        ]);
        socket.off("out");
      }
      console.log("이전 메시지확인 msgList -> ", msgList);

      setSocket(null);
      setMsgList([]);
      setChatroomname("");
      setChatroompassword("");
      setModalVisible(() => false);
    }
  };

  return (
    <>
      <Navbar
        className="bg-body-tertiary"
        expand="lg"
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <Container fluid>
          <Navbar.Brand href="/">
            <Image
              src="/img/how.png"
              style={{ height: "50px", width: "50px" }}
            />
            하루관리
          </Navbar.Brand>
          <Navbar.Collapse>
            <Nav className="me-auto">
              <Nav.Link href="/">Home</Nav.Link>
              {props.isLogin ? (
                <>
                  <Nav.Link href="/diary">사진일기</Nav.Link>
                  <Nav.Link href="/diet">식단관리</Nav.Link>
                  <Nav.Link href="/exercise">운동관리</Nav.Link>
                  <Nav.Link href="/money">지출관리</Nav.Link>
                  <Nav.Link href="/logout">로그아웃</Nav.Link>
                </>
              ) : (
                <>
                  <Nav.Link href="/login">로그인</Nav.Link>
                  <Nav.Link href="/join">회원가입</Nav.Link>
                </>
              )}
            </Nav>
            <Form onSubmit={(e) => chatStartClick(e)}>
              {" "}
              <InputGroup size="sm" style={{ width: "600px" }}>
                <InputGroup.Text>채팅방명</InputGroup.Text>
                <Form.Control
                  //type="search"
                  //className="me-2"
                  type="text"
                  name="chatroomname"
                  onChange={handleChatroomnameInput}
                  value={chatroomname}
                  ref={refChatRoom}
                />
                <InputGroup.Text>방비밀번호</InputGroup.Text>
                <Form.Control
                  //type="search"
                  //className="me-2"
                  type="text"
                  name="chatroompassword"
                  ref={refChatRoompassword}
                  onChange={handleChatroompasswordInput}
                  value={chatroompassword}
                />
                <Button variant="outline-secondary" type="submit">
                  채팅하기
                </Button>
              </InputGroup>
            </Form>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      {modalVisible && (
        <>
          <div className="modal show" style={{ display: "block" }}>
            <Modal.Dialog>
              <Modal.Header>
                <Modal.Title> 채팅방명 : {chatroomname} </Modal.Title>
                <Button
                  variant="outline-secondary"
                  style={{ textAlign: "left" }}
                  onClick={() => chatCloseModal()}
                  type="button"
                >
                  채팅창닫기
                </Button>
              </Modal.Header>
              <Modal.Body>
                <div style={{ overflow: "scroll", height: "500px" }}>
                  <ul>
                    {msgList.map((v, i) =>
                      v.type === "welcome" || v.type === "goodbye" ? (
                        <li key={i} style={{ listStyle: "none" }}>
                          <div
                            style={{
                              height: "0.5px",
                              flex: "1 1 auto",
                              padding: "0 10px",
                              backgroundColor: "#cecece",
                            }}
                          />
                          <div>{v.msg}</div>
                          <div
                            style={{
                              height: "0.5px",
                              flex: "1 1 auto",
                              padding: "0 10px",
                              backgroundColor: "#cecece",
                            }}
                          />
                        </li>
                      ) : v.type === "me" ? (
                        <li
                          style={{
                            textAlign: "left",
                            listStyle: "none",
                          }}
                          key={`${i}_1`}
                        >
                          <div
                            style={{
                              marginTop: "5px",
                              fontSize: "13px",
                              fontWeight: "bold",
                            }}
                          >
                            {v.id}
                          </div>
                          <div
                            style={{
                              padding: "5px",
                              display: "inline-block",
                              borderTopRightRadius: "20px",
                              borderBottomLeftRadius: "20px",
                              borderBottomRightRadius: "20px",
                              backgroundColor: "#cecece",
                            }}
                          >
                            {v.msg}
                          </div>
                        </li>
                      ) : (
                        <li
                          style={{
                            textAlign: "right",
                            listStyle: "none",
                          }}
                          key={`${i}_2`}
                        >
                          <div
                            style={{
                              marginTop: "5px",
                              fontSize: "13px",
                              fontWeight: "bold",
                            }}
                          >
                            {v.id}
                          </div>
                          <div
                            style={{
                              padding: "5px",
                              display: "inline-block",
                              borderTopRightRadius: "20px",
                              borderBottomLeftRadius: "20px",
                              borderBottomRightRadius: "20px",
                              backgroundColor: "#000",
                              color: "#fff",
                            }}
                          >
                            {v.msg}
                          </div>
                        </li>
                      )
                    )}
                    <li ref={refModalBody} style={{ listStyle: "none" }} />
                  </ul>
                </div>
              </Modal.Body>
              <Form onSubmit={(e) => handleSubmitChat(e)}>
                <Modal.Footer>
                  <InputGroup className="mb-3">
                    <InputGroup.Text id="inputGroup-sizing-default">
                      채팅내용
                    </InputGroup.Text>
                    <Form.Control
                      aria-label="Default"
                      aria-describedby="inputGroup-sizing-default"
                      onChange={(e) => handleMsg(e)}
                      value={msg}
                      name="chatcontents"
                    />
                  </InputGroup>

                  <Button
                    variant="outline-primary"
                    type="submit"
                    style={{ textAlign: "right" }}
                  >
                    메시지보내기
                  </Button>
                </Modal.Footer>
              </Form>
            </Modal.Dialog>
          </div>
        </>
      )}
    </>
  );
};

export default BasicMenu;
