import React, {
  useCallback,
  useEffect,
  useLayoutEffect,
  useState,
} from "react";
import { Link, Navigate, useNavigate, useSearchParams } from "react-router-dom";
import "../layout/stylesCalendar.css";
import Image from "react-bootstrap/Image";
import useCustomMove from "../../hooks/useCustomMove";
import Card from "react-bootstrap/Card";
import useCustomDiet from "../../hooks/useCustomDiet";
import useCustomLogin from "../../hooks/useCustomLogin";
import { getDietList } from "../../api/dietApi";

const chooseReturn = (number) => {
  console.log("DietListComponent.js chooseReturn() ÏßÑÏûÖ Îß§Í∞úÎ≥ÄÏàò => ", number);
  let result = "";
  for (let n of number) {
    switch (n) {
      case "1":
        return (result = "ü•¶ÏïÑÏπ®");
      case "2":
        return (result = "üçÑÏ†êÏã¨");
      case "3":
        return (result = "üßÖÏ†ÄÎÖÅ");
      case "4":
        return (result = "üßÑÍ∞ÑÏãù");
      default:
        return null;
    }
  }
  console.log(result);
};

const makeCalendar = (d) => {
  console.log("makeCalendar() ÏßÑÏûÖ Îß§Í∞úÎ≥ÄÏàò ÌôïÏù∏ => ", d);
  let date = new Date(d);
  const currentYear = new Date(date).getFullYear();
  console.log("makeCalendar() ÏßÑÏûÖ currentYear -> ", currentYear);

  const currentMonth = new Date(date).getMonth() + 1;
  console.log("makeCalendar() ÏßÑÏûÖ currentMonth -> ", currentMonth);
  const firstDay = new Date(date.setDate(1)).getDay();
  console.log("makeCalendar() ÏßÑÏûÖ firstDay -> ", firstDay);

  //ÌòÑÏû¨ Îã¨Î†•Ï≤´Ïß∏ÎÇ†
  let calfirst = "";
  function getCalfirst() {
    calfirst += `${currentYear}-`;

    if (currentMonth < 10) {
      calfirst += `0${currentMonth}`;
    } else {
      calfirst += `${currentMonth}`;
    }
    //calfirst += `01`
    return calfirst;
  } //calfirst ÌòÑÏû¨Îã¨Î†•Ï≤´ÎÇ†

  const lastDay = new Date(currentYear, currentMonth, 0).getDate();
  console.log("lastDayÍ∞í ->" + lastDay); //30
  const limitDay = firstDay + lastDay;
  console.log("limitDayÍ∞í ->" + limitDay); //31
  const nextDay = Math.ceil(limitDay / 7) * 7;
  console.log("nextDayÍ∞í ->" + nextDay); //35 35-31=4Ïπ∏ ÎπàÏπ∏ÎßåÎì§Ïñ¥ÏßÑÎã§.

  return { firstDay, lastDay, limitDay, nextDay };
};

const CalendarItem = (props) => {
  console.log("CalendarItem() ÏßÑÏûÖ");

  //UserId, content ,createdAt ,id , picture
  const { diet, dateobject } = props.i; //apiÏÑúÎ≤ÑÌõÑ dbÏóêÏÑú Î∞õÏùÄÍ∞í
  console.log("DietListComponent.js props diet=> ", diet);
  console.log("DietListComponent.js props dateobject=> ", dateobject);

  // console.log("props.i.diet[0] ", props.i.diet[0]);
  //console.log("props.i ", props.i);
  return diet.map((i) => (
    <React.Fragment key={i.id}>
      {i.id && i.choose === "1" && (
        <>
          <Link
            to={`../read/${i.choose}/${dateobject}`}
            style={{ textDecoration: "none", color: "black" }}
          >
            <Image
              src="/img/cabbage.png"
              style={{ height: "20px", width: "20px" }}
            />
            ÏïÑÏπ®
            <br />
          </Link>
        </>
      )}
      {i.id && i.choose === "2" && (
        <>
          <Link
            to={`../read/${i.choose}/${dateobject}`}
            style={{ textDecoration: "none", color: "black" }}
          >
            <Image
              src="/img/carrot.png"
              style={{ height: "20px", width: "20px" }}
            />
            Ï†êÏã¨
            <br />
          </Link>
        </>
      )}
      {i.id && i.choose === "3" && (
        <>
          <Link
            to={`../read/${i.choose}/${dateobject}`}
            style={{ textDecoration: "none", color: "black" }}
          >
            <Image
              src="/img/eggplant.png"
              style={{ height: "20px", width: "20px" }}
            />
            Ï†ÄÎÖÅ
            <br />
          </Link>
        </>
      )}
      {i.id && i.choose === "4" && (
        <>
          <Link
            to={`../read/${i.choose}/${dateobject}`}
            style={{ textDecoration: "none", color: "black" }}
          >
            <Image
              src="/img/tomato.png"
              style={{ height: "20px", width: "20px" }}
            />
            Í∞ÑÏãù
            <br />
          </Link>
        </>
      )}
    </React.Fragment>
  ));
};
console.log("Ïª¥Ìè¨ÎÑåÌä∏ Î∞ñ");

const DietListComponent = () => {
  const [searchParams] = useSearchParams();
  const { moveToCreate } = useCustomMove();
  const { effectException } = useCustomLogin();
  const navigate = useNavigate();

  const [currentMonth, setCurrentMonth] = useState("");
  const [currentYear, setCurrentYear] = useState("");
  const [currentDate, setCurrentDate] = useState("");
  const [fullcurrentDate, setFullCurrentDate] = useState("");

  const [diet, setDiet] = useState([]);

  const [searchMonth, setSearchMonth] = useState(0);
  const [searchYear, setSearchYear] = useState(0);
  const [beforeDateItemArray, setBeforeDateItemArray] = useState([]);
  const [afterDateItemArray, setAfterDateItemArray] = useState([]);
  const [dateItem, setDateItem] = useState([]);
  const [lastDay, setLastDay] = useState("");
  const [firstDay, setFirstDay] = useState("");
  const [nextDay, setNextDay] = useState("");
  const [limitDay, setLimitDay] = useState("");
  const [check, setCheck] = useState(0);
  const [make, setMake] = useState(false);

  console.log("Ïª¥Ìè¨ÎÑåÌä∏ ÏãúÏûë");

  let searchParamsYear = searchParams.get("year");
  let searchParamsMonth = searchParams.get("month");

  useLayoutEffect(() => {
    console.log("useLayoutEffect()  ÏßÑÏûÖ");
    setDateItem(() => []);
    setMake(() => false);
    let date = new Date();

    let basicYear = date.getFullYear();
    let basicMonth = date.getMonth() + 1;
    basicMonth = basicMonth < 10 ? "0" + basicMonth : basicMonth;
    date = `${basicYear}-${basicMonth}-01`;

    if (searchParamsYear !== null && searchParamsMonth !== null) {
      console.log(
        "useLayoutEffect() ÏøºÎ¶¨ÌååÎùºÎØ∏ÌÑ∞ Ï°¥Ïû¨Ìï†Í≤ΩÏö∞ ÏßÑÏûÖ -> ",
        searchParamsYear
      );
      console.log(
        "useLayoutEffect() ÏøºÎ¶¨ÌååÎùºÎØ∏ÌÑ∞ Ï°¥Ïû¨Ìï†Í≤ΩÏö∞ ÏßÑÏûÖ -> ",
        searchParamsMonth
      );
      setSearchMonth(() => Number(searchParamsMonth));
      setSearchYear(() => searchParamsYear);

      date = new Date(`${searchParamsYear}-${searchParamsMonth}-01`);
      console.log("date-> ", date);
    }
    console.log("useLayoutEffect()  setCurrentDate ÎÑ£Í∏∞Ï†Ñ-> ", date);

    setCurrentDate(() => date); //2025-03-25
    const longdate = new Date(date);
    setCurrentYear("" + longdate.getFullYear());
    setCurrentMonth(longdate.getMonth() + 1);
    setFullCurrentDate(() => longdate); //Sat Mar 01 2025 09:00:00 GMT+0900 (ÌïúÍµ≠ ÌëúÏ§ÄÏãú)
  }, [searchParamsYear, searchParamsMonth]);

  useEffect(() => {
    if (fullcurrentDate !== "") {
      console.log(
        "useEffect() fullcurrentDate Ï°¥Ïû¨Ìï†Í≤ΩÏö∞ =>  ",
        fullcurrentDate
      );

      const { firstDay, nextDay, limitDay, lastDay } =
        makeCalendar(fullcurrentDate);

      setLastDay(lastDay);
      setFirstDay(firstDay);
      setNextDay(nextDay);
      setLimitDay(limitDay);
    } //if

    return () => {
      console.log("useEffect() makeCalendar() Î∞òÌôòÎ≥ÄÏàòÎì§ Ï¥àÍ∏∞Ìôî");

      setLastDay(() => "");
      setFirstDay(() => "");
      setNextDay(() => "");
      setLimitDay(() => "");
    };
  }, [fullcurrentDate]);

  //Îã¨Î†•ÏóêÏÑú Ïù¥Ï†ÑÎπàÏπ∏
  useEffect(() => {
    console.log("useEffect() Îã¨Î†• Îç∞Ïù¥ÌÑ∞ÎÑ£Í∏∞ ÏßÑÏûÖ");

    if (make === true) {
      console.log("useEffect() Îã¨Î†• Îç∞Ïù¥ÌÑ∞ÎÑ£Í∏∞ ÏßÑÏûÖ makeÏÉÅÌÉúÍ∞Ä trueÏßÑÏûÖ");

      let dateItem = [];

      for (let i = 1, j = 0; i <= lastDay; i++, j++) {
        console.log("useEffect() Îã¨Î†• Îç∞Ïù¥ÌÑ∞ÎÑ£Í∏∞ forÎ¨∏ ÏßÑÏûÖ");

        let currentYear = new Date(fullcurrentDate).getFullYear();
        let currentMonth = new Date(fullcurrentDate).getMonth() + 1;
        currentMonth = currentMonth < 10 ? "0" + currentMonth : currentMonth;
        let ichange = i < 10 ? "0" + i : i;
        let dateobject = `${currentYear}-${currentMonth}-${ichange}`;

        dateItem[j] = {
          dateitem: i,
          dateobject: dateobject,
          diet: diet.filter((a) => a.dateobject === dateobject),
        };
      }
      setDateItem(() => dateItem);

      const beforeDateItem = firstDay - 0;
      let beforeDateItemArray = [];

      if (beforeDateItem !== 0) {
        for (let i = 1, j = 0; i <= beforeDateItem; i++, j++) {
          beforeDateItemArray[j] = i;
        }
      }
      setBeforeDateItemArray(() => beforeDateItemArray);

      //Îã¨Î†•ÏóêÏÑú Ïù¥ÌõÑÎπàÏπ∏
      const afterDateItem = nextDay - limitDay;
      let afterDateItemArray = [];

      if (afterDateItem !== 0) {
        for (let i = 1, j = 0; i <= afterDateItem; i++, j++) {
          afterDateItemArray[j] = i;
        }
      }
      setAfterDateItemArray(() => afterDateItemArray);
    }
  }, [make]);

  useEffect(() => {
    console.log("useEffect() ÏßÑÏûÖ axios ÎπÑÎèôÍ∏∞ÏöîÏ≤≠ ");
    if (currentDate !== "") {
      console.log(
        "useEffect() ÏßÑÏûÖ axios ÎπÑÎèôÍ∏∞ÏöîÏ≤≠ currentDate Ï°¥Ïû¨Ìï†Í≤ΩÏö∞ ->",
        currentDate
      );

