import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import { useSelector } from "react-redux";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import Button from "react-bootstrap/Button";
import Image from "react-bootstrap/Image";
//채팅
import { io } from "socket.io-client";
import { useEffect, useRef, useState } from "react";

import Modal from "react-bootstrap/Modal";
import useCustomLogin from "../../hooks/useCustomLogin";

const BasicMenu = (props) => {
  const { loginState, isLogin } = useCustomLogin();
  const [modalVisible, setModalVisible] = useState(false);
  //채팅
  const [msg, setMsg] = useState({});
  const [msgList, setMsgList] = useState([]);
  const [chatroom, setChatroom] = useState("");
  const [chatfriend, setChatfriend] = useState("");
  const [chatfriendArray, setChatfriendArray] = useState([]);

  const refChatRoom = useRef();

  const refModalBody = useRef();
  //소켓
  const [socket, setSocket] = useState(null);

  const handleMsg = (e) => {
    //console.log("handleMsg 이벤트핸들러 진입 ->", e.target.value);
    setMsg(e.target.value);
  };

  //useEffect(() => {});
  const chatStartClick = (e) => {
    e.preventDefault();
    if (!isLogin) {
      alert("로그인 후 이용가능합니다.");

      return;
    }

    if (!e.target.chatroom.value) {
      alert("방 이름을 입력하세요!");
      return;
    }

    const socket = io("http://localhost:8001", {
      autoConnect: false,
      // cors: {
      //   origin: "http://localhost:3000",
      // },
      query: {
        username: loginState, //email
        chatroom: e.target.chatroom.value,
      },
    });
    socket.connect();
    setSocket(() => socket);
    setChatroom(() => e.target.chatroom.value);

    function loginCallback(message) {
      console.log("loginCacllback 소켓 login 이벤트 받은 결과 -> ", message);
      setMsgList((prev) => [
        ...prev,
        {
          msg: message,
          type: "welcome",
          id: "",
        },
      ]);
      setModalVisible(() => true);
      refModalBody.current?.scrollIntoView({
        behavior: "smooth",
        block: "end",
      });
    } //loginCallback
    socket.on("login", loginCallback);
  }; //chatStartClick

  const dd = () => {
    function otherCallback(message) {
      const { data, id } = message;
      setMsgList((prev) => [...prev, { msg: data, type: "other", id: id }]);
    }

    console.log("socket.on('other') =>", socket.on("other"));

    if (socket.on("other")) {
      socket.on("other", otherCallback);
      refModalBody.current?.scrollIntoView({
        behavior: "smooth",
        block: "end",
      });
    }
    refModalBody.current?.scrollIntoView({ behavior: "smooth", block: "end" });
  };

  const handleSubmitChat = (e) => {
    e.preventDefault();

    console.log("handleSubmitChat() 진입 => ", e.target.chatcontents.value);
    const sendData = {
      data: e.target.chatcontents.value,
      username: loginState,
      chatroom: chatroom,
    };
    socket.emit("message", sendData);
    setMsgList((prev) => [
      ...prev,
      { msg: msg, type: "me", id: loginState }, //email
    ]);
    refModalBody.current?.scrollIntoView({ behavior: "smooth", block: "end" });

    setMsg(() => "");
  };

  const chatCloseModal = () => {
    if (
      window.confirm(
        "채팅방에서 나가시면 모든 내용이 삭제됩니다 나가시겠습니까?"
      )
    ) {
      function goodbyeCallback(message) {
        setMsgList((prev) => [
          ...prev,
          {
            msg: message,
            type: "goodbye",
            id: "",
          },
        ]);
      }
      socket.emit("disconnect", { chatroom: chatroom });
      socket.on("disconnect", goodbyeCallback);

      setSocket(() => null);
      setMsgList(() => []);
      refChatRoom.current.value = "";
      setModalVisible(() => false);
    }
  };

  const chatInvitation = () => {
    if (!chatfriend) {
      alert("채팅할 친구를 입력해주세요!");
      return;
    }
    const sendData = {
      friend: chatfriend,
      //id: loginState,
      chatroom: chatroom,
    };
    socket.emit("friend", sendData);

    function friendCallback(message) {
      console.log("chatInvitation 소켓 friend 이벤트 받은 결과 -> ", message);

      if (loginState === message.friend) {
        setMsgList((prev) => [
          ...prev,
          {
            msg: message,
            type: "welcome",
            id: "",
          },
        ]);
      }
    }
    socket.on("friend", friendCallback);
    refModalBody.current?.scrollIntoView({ behavior: "smooth", block: "end" });
    //친구존재시 보여주기 친구화면에 채팅창 보이기
    setModalVisible(() => true);
    setChatfriend(() => "");
  };

  return (
    <>
      <Navbar
        className="bg-body-tertiary"
        expand="lg"
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <Container fluid>
          <Navbar.Brand href="/">
            <Image
              src="/img/how.png"
              style={{ height: "50px", width: "50px" }}
            />
            하루관리
          </Navbar.Brand>
          <Navbar.Collapse>
            <Nav className="me-auto">
              <Nav.Link href="/">Home</Nav.Link>
              {props.isLogin ? (
                <>
                  <Nav.Link href="/diary">사진일기</Nav.Link>
                  <Nav.Link href="/diet">식단관리</Nav.Link>
                  <Nav.Link href="/exercise">운동관리</Nav.Link>
                  <Nav.Link href="/money">지출관리</Nav.Link>
                  <Nav.Link href="/logout">로그아웃</Nav.Link>
                </>
              ) : (
                <>
                  <Nav.Link href="/login">로그인</Nav.Link>
                  <Nav.Link href="/join">회원가입</Nav.Link>
                </>
              )}
            </Nav>
            <Form onSubmit={(e) => chatStartClick(e)}>
              {" "}
              <InputGroup size="sm" style={{ width: "300px" }}>
                <Form.Control
                  //type="search"
                  //className="me-2"
                  type="text"
                  name="chatroom"
                  placeholder="로그인 후 채팅방 이름을 입력하세요"
                  ref={refChatRoom}
                />
                <Button variant="outline-secondary" type="submit">
                  채팅하기
                </Button>
              </InputGroup>
            </Form>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      {modalVisible && (
        <>
          <div className="modal show" style={{ display: "block" }}>
            <Modal.Dialog>
              <Modal.Header>
                <Modal.Title> 채팅방명 : {chatroom} </Modal.Title>
                <br />
                <InputGroup className="mb-3">
                  <InputGroup.Text id="inputGroup-sizing-default">
                    초대할아이디(email)
                  </InputGroup.Text>
                  <Form.Control
                    aria-label="Default"
                    aria-describedby="inputGroup-sizing-default"
                    name="chatfriend"
                    value={chatfriend}
                  />
                </InputGroup>

                <Button
                  variant="outline-secondary"
                  style={{ textAlign: "left" }}
                  onClick={() => chatInvitation()}
                  type="button"
                >
                  채팅초대하기
                </Button>
              </Modal.Header>
              <Modal.Body>
                <div
                  style={{ overflow: "scroll", height: "500px" }}
                  ref={refModalBody}
                >
                  <ul>
                    {msgList.map((v, i) =>
                      v.type === "welcome" || v.type === "goodbye" ? (
                        <li
                          className="welcome"
                          key={i}
                          style={{ listStyle: "none" }}
                        >
                          <div
                            style={{
                              height: "0.5px",
                              flex: "1 1 auto",
                              padding: "0 10px",
                              backgroundColor: "#cecece",
                            }}
                          />
                          <div>{v.msg}</div>
                          <div className="line" />
                        </li>
                      ) : v.type === "me" ? (
                        <li
                          style={{
                            textAlign: "left",
                            listStyle: "none",
                          }}
                          key={`${i}_li`}
                        >
                          <div
                            style={{
                              marginTop: "5px",
                              fontSize: "13px",
                              fontWeight: "bold",
                            }}
                          >
                            {v.id}
                          </div>
                          <div
                            style={{
                              padding: "5px",
                              display: "inline-block",
                              borderTopRightRadius: "20px",
                              borderBottomLeftRadius: "20px",
                              borderBottomRightRadius: "20px",
                              backgroundColor: "#cecece",
                            }}
                          >
                            {v.msg}
                          </div>
                        </li>
                      ) : (
                        <li
                          style={{
                            textAlign: "right",
                            listStyle: "none",
                          }}
                          key={`${i}_li`}
                        >
                          <div
                            style={{
                              marginTop: "5px",
                              fontSize: "13px",
                              fontWeight: "bold",
                            }}
                          >
                            {v.id}
                          </div>
                          <div
                            style={{
                              padding: "5px",
                              display: "inline-block",
                              borderTopRightRadius: "20px",
                              borderBottomLeftRadius: "20px",
                              borderBottomRightRadius: "20px",
                              backgroundColor: "#000",
                              color: "#fff",
                            }}
                          >
                            {v.msg}
                          </div>
                        </li>
                      )
                    )}
                  </ul>
                </div>
              </Modal.Body>
              <Form onSubmit={(e) => handleSubmitChat(e)}>
                <Modal.Footer>
                  <InputGroup className="mb-3">
                    <InputGroup.Text id="inputGroup-sizing-default">
                      채팅내용
                    </InputGroup.Text>
                    <Form.Control
                      aria-label="Default"
                      aria-describedby="inputGroup-sizing-default"
                      onChange={(e) => handleMsg(e)}
                      name="chatcontents"
                    />
                  </InputGroup>

                  <Button
                    variant="outline-secondary"
                    style={{ textAlign: "left" }}
                    onClick={() => chatCloseModal()}
                    type="button"
                  >
                    채팅창닫기
                  </Button>
                  <Button
                    variant="outline-primary"
                    type="submit"
                    style={{ textAlign: "right" }}
                  >
                    메시지보내기
                  </Button>
                </Modal.Footer>
              </Form>
            </Modal.Dialog>
          </div>
        </>
      )}
    </>
  );
};

export default BasicMenu;
