import Container from "react-bootstrap/Container";
import Card from "react-bootstrap/Card";
import ListGroup from "react-bootstrap/ListGroup";
import useCustomLogin from "../../hooks/useCustomLogin";
import React, {
  useCallback,
  useEffect,
  useLayoutEffect,
  useState,
} from "react";
import { weatherapi } from "../../api/openApi";
import Form from "react-bootstrap/Form";
import Image from "react-bootstrap/Image";
import Button from "react-bootstrap/esm/Button";
import { getTodayManagement } from "../../api/mainApi";
import { Link } from "react-router-dom";
import useCustomMove from "../../hooks/useCustomMove";

const switchFn = (category, obsrValue) => {
  let result = "";
  //  console.log("T1H 진입");

  switch (category) {
    case "T1H":
      result += `기온 : ${obsrValue}`;
      break;
    case "PTY":
      result += `강수 : ${obsrValue}`;
      break;
    case "REH":
      result += `습도 : ${obsrValue}`;
      break;
    case "RN1":
      result += `강수량 : ${obsrValue}`;
      break;
    case "WSD":
      result += `풍속 : ${obsrValue}`;
      break;
    default:
      break;
  }
  //console.log("switchFn 결과 -> ", result);
  return result;
}; //switchFn

const MainComponent = () => {
  const { loginState, isLogin } = useCustomLogin();
  console.log(isLogin);

  const { exceptionHandle } = useCustomLogin();
  console.log(loginState); //user, accessToken 2개 들어있다 이렇게 슬라이스에보관

  //공공api
  const [weatherVisible, setWeatherVisible] = useState(false);
  const [weatherResult, setWeatherResult] = useState([]);
  const [currentDate, setCurrentDate] = useState("");
  const [weatherTime, setWeatherTime] = useState("");
  const [weatherDate, setWeatherDate] = useState("");
  const [weatherStandard, setWeatherStandard] = useState("");
  const [selectMonth, setSelectMonth] = useState(3);
  const [selectYear, setSelectYear] = useState(2025);
  const [dateArray, setDateArray] = useState([]);
  const [dietResultArray, setDietResultArray] = useState([]);
  const [diaryResultArray, setDiaryResultArray] = useState([]);
  const [exerciseResultArray, setExerciseResultArray] = useState([]);
  const [moneyResultArray, setMoneyResultArray] = useState([]);
  const [findDate, setFindDate] = useState("");
  const [todayDate, setTodayDate] = useState("");
  const [dietId, setDietId] = useState("");
  const { moveToLogin } = useCustomMove();
  const { dietMorning, setDietMorning } = useState([]);
  const { dietLunch, setDietLunch } = useState([]);
  const { dietEvening, setDietEvening } = useState([]);
  const { dietSnack, setDietSnack } = useState([]);
  //
  //  base_date: "20250310",
  // base_time: "2200",

  useLayoutEffect(() => {
    console.log(
      "MainComponent.js weatherapiEI 내 useLayoutEffect currentDateFn() 진입"
    );

    if (!isLogin) {
      alert("로그인이 필요합니다!");
      return moveToLogin();
    }

    let today = new Date();
    // 현재 날짜를 가져옵니다.

    setTodayDate(() => today.getDate());

    let hour =
      today.getHours() < 10 ? "0" + today.getHours() : today.getHours();
    let minutes =
      today.getMinutes() < 10 ? "0" + today.getMinutes() : today.getMinutes();
    minutes = minutes < 30 ? "00" : "30";

    setWeatherTime(() => hour + minutes); //위치기반한 날씨위한 시간
    setWeatherStandard(() => `${hour}시 ${minutes}분`); //위치기반한 날씨위한 날짜
    setCurrentDate(
      //화면에 보일 오늘날짜
      () =>
        `${today.getFullYear()}년 ${
          today.getMonth() + 1
        }월 ${today.getDate()}일`
    );

    let monthfinal =
      today.getMonth() + 1 < 10
        ? "0" + (today.getMonth() + 1) // `"0"${today.getMonth() + 1}`
        : today.getMonth() + 1;
    let datefinal =
      today.getDate() < 10 ? "0" + today.getDate() : today.getDate();
    setWeatherDate(() => `${today.getFullYear()}${monthfinal}${datefinal}`);
    setFindDate(() => `${today.getFullYear()}-${monthfinal}-${datefinal}`);
  }, []);

  useLayoutEffect(() => {
    console.log(
      "MainComponent.js 위치기반 날씨 axios 요청할 useLayoutEffect() 진입"
    );

    console.log("MainComponent.js  weatherTime ", weatherTime);
    console.log("MainComponent.js  weatherDate ", weatherDate);

    const weatherapiEI = async () => {
      console.log("MainComponent.js weatherapiEI 내 weatherapi() 진입 ");

      const result = await weatherapi({ weatherDate, weatherTime });
      if (Array.isArray(result)) {
        //console.log("BasicMenu.js useEffect() 내 공공API 결과확인 ", result); //배열로받음
        let newObj = [];
        //let num =1;

        result.forEach((r) => {
          let newO = {};
          //newO["num"] =num ;
          newO["category"] = r.category;
          newO["obsrValue"] = r.obsrValue;
          //num++;
          return newObj.push(newO);
        });
        // console.log(
        //   "BasicMenu.js useEffect() 배열로 category만 남김 ",
        //   newObj
        // ); //배열로 category만 남김

        setWeatherResult(() => {
          return newObj;
        });
        setWeatherVisible(true);
        // console.log(
        //   "BasicMenu.js useEffect() weatehrReuslt => ",
        //   weatherResult
        // );
      } //if배열일경우
    }; //weatherapiEI

    weatherapiEI();
  }, [weatherDate]);

  useLayoutEffect(() => {
    console.log(
      "useLayoutEffect() getTodayManagementEI() 진입 findDate =>",
      findDate
    );

    const getTodayManagementEI = async () => {
      await getTodayManagement(findDate)
        .then((result) => {
          console.log("useEffect()진입 axios 결과 -> ", result);

          setDiaryResultArray(result.diary);
          setDietResultArray(() => result.diet[0]?.Dietdetails);

          result.diet[0]?.Dietdetails.forEach((v) => {
            if (v.choose === "1") {
              setDietMorning((prev) => [...prev, v]);
            } else if (v.choose === "2") {
              setDietLunch((prev) => [...prev, v]);
            } else if (v.choose === "3") {
              setDietEvening((prev) => [...prev, v]);
            } else {
              setDietSnack((prev) => [...prev, v]);
            }
          });
          setDietId(result.diet[0]?.Dietdetails[0]?.DietId);
          setMoneyResultArray(result.money);
          setExerciseResultArray(result.exercice);
        })
        .catch((err) => {
          console.log("useEffect()진입 axios 에러-> ", err);
          //          exceptionHandle(err);
        });
    }; //getTodayManagementEI()
    getTodayManagementEI();
  }, [findDate]);

  useEffect(() => {
    console.log("useEffect()진입 일자만 출력");

    const getDate = async () => {
      console.log("getDate() 진입");
      console.log("getDate() 진입 selectYear => ", selectYear);
      console.log("getDate() 진입 selectMonth => ", selectMonth);

      let getDateResult = [];
      let firstDay = new Date(selectYear, selectMonth - 1, 1).getDate();
      let lastDay = new Date(selectYear, selectMonth, 0).getDate();
      console.log("getDate() firstDay => ", firstDay);
      console.log("getDate() lastDay=> ", lastDay);

      for (let i = firstDay; i <= lastDay; i++) {
        getDateResult.push(i);
      }
      setDateArray(() => getDateResult);
    };
    getDate(); //해당 월에해당하는 일자들 배열로 setState()넣기

    console.log("axios 결과 diaryResultArray => ", diaryResultArray);
    console.log("axios 결과 dietResultArray=> ", dietResultArray);
    console.log("axios 결과 exerciseResultArray => ", exerciseResultArray);
    console.log("axios 결과 moneyResultArray=> ", moneyResultArray);

    return () => {
      setDateArray([]);
    };
  }, []);

  const handleSelectYear = (e) => {
    setSelectYear(() => Number(e.target.value));
  };

  const handleSelectMonth = (e) => {
    setSelectMonth(() => Number(e.target.value));
  };

  const handleDateManagement = (d) => {
    console.log("handleDateManagement()진입");
    let selectMonthAdd = selectMonth < 10 ? "0" + selectMonth : selectMonth;
    let dAdd = d < 10 ? "0" + d : d;

    setFindDate(() => `${selectYear}-${selectMonthAdd}-${dAdd}`);
    console.log("findDate -> ", findDate);

    getTodayManagement(findDate)
      .then((result) => {
        console.log("handleDateManagement()진입 axios 결과 -> ", result);

        setDiaryResultArray(() => result.diary);
        setDietResultArray(() => result.diet[0]?.Dietdetails);
        setDietId(result.diet[0]?.Dietdetails[0]?.DietId);
        setMoneyResultArray(() => result.money);
        setExerciseResultArray(() => result.exercice);
      })
      .catch((err) => {
        console.log("handleDateManagement()진입 axios 에러-> ", err);
      });
  };

  return (
    <>
      <Container>
        <br />
        <br />
        {isLogin && loginState ? (
          <>
            <span style={{ fontSize: "25px" }}>
              {" "}
              {loginState} 님의 하루관리
            </span>
          </>
        ) : (
          <>
            <span style={{ fontSize: "25px" }}>하루관리</span>
          </>
        )}
        <br />
        &nbsp;&nbsp;{" "}
        <Image
          src="/img/calendar.png"
          style={{ width: "30px", height: "30px" }}
          rounded
        />{" "}
        &nbsp;오늘은 &nbsp;
        {currentDate} &nbsp;&nbsp;
        {weatherVisible &&
          weatherResult.map((w, i) => (
            <>
              <span key={i}> {switchFn(w.category, w.obsrValue)}</span>;
            </>
          ))}
        ({weatherStandard}기준)
      </Container>

      <hr />
      <br />

      <Container>
        <Form.Select
          style={{ width: "200px", display: "inline-block" }}
          value={selectYear}
          onChange={(e) => handleSelectYear(e)}
        >
          <option value="2025">2025</option>
          <option value="2026">2026</option>
        </Form.Select>
        <Form.Select
          style={{ width: "200px", display: "inline-block" }}
          value={selectMonth}
          onChange={(e) => handleSelectMonth(e)}
        >
          <option value="1">1</option>
          <option value="2">2</option>
          <option value="3">3</option>
          <option value="4">4</option>
          <option value="5">5</option>
          <option value="6">6</option>
          <option value="7">7</option>
          <option value="8">8</option>
          <option value="9">9</option>
          <option value="10">10</option>
          <option value="11">11</option>
          <option value="12">12</option>
        </Form.Select>
        &nbsp;&nbsp;*날짜클릭시 작성한 하루관리를 한눈에 볼수있습니다!
        <br />
        <br />
        {Array.isArray(dateArray) &&
          dateArray.length !== 0 &&
          dateArray.map((d, i) =>
            d === todayDate ? (
              <span key={i} style={{ fontSize: "20px" }}>
                <Button
                  size="sm"
                  variant="secondary"
                  onClick={() => handleDateManagement(d)}
                >
                  {d}
                </Button>
                &nbsp;
              </span>
            ) : (
              <span key={i} style={{ fontSize: "20px" }}>
                <Button
                  size="sm"
                  variant="outline-secondary"
                  onClick={() => handleDateManagement(d)}
                >
                  {d}
                </Button>
                &nbsp;
              </span>
            )
          )}
        <br />
        <br />
      </Container>

      <Container style={{ textAlign: "center" }}>
        {Array.isArray(diaryResultArray) && diaryResultArray.length === 0 ? (
          <>
            <Card style={{ width: "18rem", display: "inline-block" }}>
              <Card.Body>
                <Card.Title>사진다이어리</Card.Title>
              </Card.Body>
              <ListGroup className="list-group-flush">
                <ListGroup.Item>
                  오늘 다이어리를 아직 작성하지 않았어요! <br />
                  <Button size="sm" variant="outline-primary">
                    <Link
                      style={{ textDecoration: "none" }}
                      to={`../diary/create/${findDate}`}
                    >
                      다이어리작성
                    </Link>
                  </Button>
                </ListGroup.Item>
              </ListGroup>
            </Card>
          </>
        ) : (
          diaryResultArray.map((d, i) => (
            <Card style={{ width: "18rem", display: "inline-block" }} key={i}>
              {d.picture ? (
                <Card.Img
                  variant="top"
                  src={`http://localhost:8001/img/${d.picture}`}
                  style={{
                    display: "block",
                    margin: "0 auto",
                    width: "200px",
                    height: "200px",
                    objectFit: "contain",
                  }}
                />
              ) : (
                <>업로드하지 않음😅</>
              )}

              <Card.Body>
                <Card.Title>사진다이어리</Card.Title>
                {/* <Card.Text>내용</Card.Text> */}
              </Card.Body>
              <ListGroup className="list-group-flush">
                <ListGroup.Item>{d.content}</ListGroup.Item>
              </ListGroup>
              <Card.Body>
                <Card.Link
                  style={{ textDecoration: "none" }}
                  to={`../diary/modity/${d.id}`}
                >
                  <Button variant="outline-warning">수정/삭제</Button>
                </Card.Link>

                <Card.Link
                  to={"../diary/list"}
                  style={{ textDecoration: "none" }}
                >
                  {" "}
                  <Button variant="outline-primary">다이어리이동</Button>
                </Card.Link>
              </Card.Body>
            </Card>
          ))
        )}
        &nbsp; &nbsp;
        {Array.isArray(dietResultArray) && dietResultArray.length === 0 ? (
          <>
            <Card style={{ width: "18rem", display: "inline-block" }}>
              <Card.Body>
                <Card.Title>식단</Card.Title>
              </Card.Body>{" "}
              <ListGroup className="list-group-flush">
                <ListGroup.Item>
                  오늘 식단을 아직 작성하지 않았어요! <br />
                  <Button size="sm" variant="outline-primary">
                    <Link
                      style={{ textDecoration: "none" }}
                      to={`../diet/create/${findDate}`}
                    >
                      식단작성
                    </Link>
                  </Button>
                </ListGroup.Item>
              </ListGroup>
            </Card>
          </>
        ) : (
          <Card style={{ width: "18rem", display: "inline-block" }}>
            <Card.Body>
              <Card.Title>식단</Card.Title>
              {/* <Card.Text>식단</Card.Text> */}
            </Card.Body>
            <ListGroup className="list-group-flush">
              {dietMorning.length !== 0 &&
                dietMorning.map((d, i) => (
                  <ListGroup.Item>
                    아침
                    <React.Fragment key={i}>
                      {d.content} {d.quantity}
                    </React.Fragment>
                  </ListGroup.Item>
                ))}
              {dietLunch.length !== 0 &&
                dietLunch.map((d, i) => (
                  <ListGroup.Item>
                    점심
                    <React.Fragment key={i}>
                      {d.content} {d.quantity}
                    </React.Fragment>
                  </ListGroup.Item>
                ))}

              {dietEvening.length !== 0 &&
                dietEvening.map((d, i) => (
                  <ListGroup.Item>
                    저녁
                    <React.Fragment key={i}>
                      {d.content} {d.quantity}
                    </React.Fragment>
                  </ListGroup.Item>
                ))}

              {dietSnack.length !== 0 &&
                dietSnack.map((d, i) => (
                  <ListGroup.Item>
                    간식
                    <React.Fragment key={i}>
                      {d.content} {d.quantity}
                    </React.Fragment>
                  </ListGroup.Item>
                ))}
            </ListGroup>
            <Card.Body>
              <Card.Link
                style={{ textDecoration: "none" }}
                to={`../diary/modity/${dietId}`}
              >
                <Button variant="outline-warning">수정/삭제</Button>
              </Card.Link>
              <Card.Link
                style={{ textDecoration: "none" }}
                to={`../diary/modity/${dietId}`}
              >
                <Button variant="outline-primary">식단관리이동</Button>
              </Card.Link>
            </Card.Body>
          </Card>
        )}
        &nbsp; &nbsp;
        {Array.isArray(moneyResultArray) && moneyResultArray.length === 0 ? (
          <>
            <Card style={{ width: "18rem", display: "inline-block" }}>
              <Card.Body>
                <Card.Title>가계부</Card.Title>
              </Card.Body>
              <ListGroup className="list-group-flush">
                <ListGroup.Item>
                  오늘 가계부를 아직 작성하지 않았어요! <br />
                  <Button size="sm" variant="outline-primary">
                    <Link
                      style={{ textDecoration: "none" }}
                      to={`../money/create/${findDate}`}
                    >
                      가계부작성
                    </Link>{" "}
                  </Button>
                </ListGroup.Item>
              </ListGroup>
            </Card>
          </>
        ) : (
          moneyResultArray.map((m, i) => (
            <Card style={{ width: "18rem", display: "inline-block" }} key={i}>
              <Card.Body>
                <Card.Title>가계부</Card.Title>
                {/* <Card.Text>가계부</Card.Text> */}
              </Card.Body>
              <ListGroup className="list-group-flush">
                <ListGroup.Item>수입</ListGroup.Item>
                <ListGroup.Item>지출</ListGroup.Item>
                <ListGroup.Item>이달합계</ListGroup.Item>
              </ListGroup>
              <Card.Body>
                <Card.Link
                  style={{ textDecoration: "none" }}
                  to={`../diary/modity/${m.id}`}
                >
                  <Button variant="outline-warning">수정/삭제</Button>
                </Card.Link>
                <Card.Link
                  style={{ textDecoration: "none" }}
                  to={`../diary/modity/${m.id}`}
                >
                  <Button variant="outline-warning">수입지출이동</Button>
                </Card.Link>
              </Card.Body>
            </Card>
          ))
        )}
        &nbsp; &nbsp;
        {Array.isArray(exerciseResultArray) &&
        exerciseResultArray.length !== 0 ? (
          exerciseResultArray.map((e, i) => (
            <Card style={{ width: "18rem", display: "inline-block" }} key={i}>
              <Card.Body>
                <Card.Title>운동</Card.Title>
                {/* <Card.Text>운동</Card.Text> */}
              </Card.Body>
              <ListGroup className="list-group-flush">
                <ListGroup.Item>운동내용</ListGroup.Item>
              </ListGroup>
              <Card.Body>
                <Card.Link
                  style={{ textDecoration: "none" }}
                  to={`../diary/modity/${e.id}`}
                >
                  <Button variant="outline-warning">수정/삭제</Button>
                </Card.Link>
                <Card.Link
                  style={{ textDecoration: "none" }}
                  to={`../diary/modity/${e.id}`}
                >
                  <Button variant="outline-warning">운동이동</Button>
                </Card.Link>
              </Card.Body>
            </Card>
          ))
        ) : (
          <Card style={{ width: "18rem", display: "inline-block" }}>
            <Card.Body>
              <Card.Title>운동</Card.Title>
            </Card.Body>

            <ListGroup className="list-group-flush">
              <ListGroup.Item>
                오늘 운동을 아직 작성하지 않았어요! <br />
                <Button size="sm" variant="outline-primary">
                  <Link
                    style={{ textDecoration: "none" }}
                    to={`../exercise/create/${findDate}`}
                  >
                    운동작성
                  </Link>
                </Button>
              </ListGroup.Item>
            </ListGroup>
          </Card>
        )}
        <br />
      </Container>
      <br />
      <br />
      <br />
    </>
  );
};
export default MainComponent;
