import Container from "react-bootstrap/Container";
import Card from "react-bootstrap/Card";
import ListGroup from "react-bootstrap/ListGroup";
import useCustomLogin from "../../hooks/useCustomLogin";
import { useEffect, useState } from "react";
import { weatherapi } from "../../api/openApi";
import Form from "react-bootstrap/Form";
import Image from "react-bootstrap/Image";
import Button from "react-bootstrap/esm/Button";

const switchFn = (category, obsrValue) => {
  let result = "";
  console.log("T1H 진입");

  switch (category) {
    case "T1H":
      result += `기온 : ${obsrValue}`;
      break;
    case "PTY":
      result += `강수 : ${obsrValue}`;
      break;
    case "REH":
      result += `습도 : ${obsrValue}`;
      break;
    case "RN1":
      result += `강수량 : ${obsrValue}`;
      break;
    case "WSD":
      result += `풍속 : ${obsrValue}`;
      break;
    default:
      break;
  }
  console.log("switchFn 결과 -> ", result);
  return result;
};

const MainComponent = () => {
  const { loginState, isLogin } = useCustomLogin();
  console.log(isLogin);

  console.log(loginState); //user, accessToken 2개 들어있다 이렇게 슬라이스에보관

  //공공api
  const [weatherVisible, setWeatherVisible] = useState(false);
  const [weatherResult, setWeatherResult] = useState([]);
  const [currentDate, setCurrentDate] = useState("");
  const [weatherTime, setWeatherTime] = useState("");
  const [weatherDate, setWeatherDate] = useState("");
  const [weatherStandard, setWeatherStandard] = useState("");
  const [selectMonth, setSelectMonth] = useState(3);
  const [selectYear, setSelectYear] = useState(2025);
  //
  //  base_date: "20250310",
  // base_time: "2200",

  useEffect(() => {
    const currentDateFn = () => {
      console.log("MainComponent.js weatherapiEI 내 currentDateFn() 진입");

      let today = new Date();
      // 현재 날짜를 가져옵니다.

      let hour =
        today.getHours() < 10 ? "0" + today.getHours() : today.getHours();
      let minutes =
        today.getMinutes() < 10 ? "0" + today.getMinutes() : today.getMinutes();
      minutes = minutes < 30 ? "00" : "30";

      setWeatherTime(() => hour + minutes); //위치기반한 날씨위한 시간
      setWeatherStandard(() => `${hour}시 ${minutes}분`); //위치기반한 날씨위한 날짜
      setCurrentDate(
        //화면에 보일 오늘날짜
        () =>
          `${today.getFullYear()}년 ${
            today.getMonth() + 1
          }월 ${today.getDate()}일`
      );

      let monthfinal =
        today.getMonth() + 1 < 10
          ? "0" + (today.getMonth() + 1) // `"0"${today.getMonth() + 1}`
          : today.getMonth() + 1;
      let datefinal =
        today.getDate() < 10 ? "0" + today.getDate() : today.getDate();
      setWeatherDate(() => `${today.getFullYear()}${monthfinal}${datefinal}`);
    };
    currentDateFn();
    const weatherapiEI = async () => {
      console.log(
        "MainComponent.js weatherapiEI 내 weatherapi() 진입 weatherDate => ",
        weatherDate
      );
      console.log(
        "MainComponent.js weatherapiEI 내 weatherapi() 진입 weatherTime => ",
        weatherTime
      );

      const result = await weatherapi({ weatherDate, weatherTime });
      if (Array.isArray(result)) {
        console.log("BasicMenu.js useEffect() 내 공공API 결과확인 ", result); //배열로받음
        let newObj = [];
        result.forEach((r) => {
          let newO = {};
          newO["category"] = r.category;
          newO["obsrValue"] = r.obsrValue;
          return newObj.push(newO);
        });
        console.log("BasicMenu.js useEffect() 배열로 category만 남김 ", newObj); //배열로 category만 남김

        setWeatherResult(() => {
          return newObj;
        });
        setWeatherVisible(true);
        console.log(
          "BasicMenu.js useEffect() weatehrReuslt => ",
          weatherResult
        );
      }
    };
    weatherapiEI();
  }, []);

  const getDate = () => {
    console.log("getDate() 진입");
    console.log("getDate() 진입 selectYear => ", selectYear);
    console.log("getDate() 진입 selectMonth => ", selectMonth);

    let getDateResult = [];
    let firstDay = new Date(selectYear, selectMonth, 1).getDate();
    let lastDay = new Date(selectYear, selectMonth + 1, 0).getDate();
    console.log("getDate() firstDay => ", firstDay);
    console.log("getDate() lastDay=> ", lastDay);

    for (let i = firstDay; i <= lastDay; i++) {
      getDateResult.push(i);
    }
    return getDateResult;
  };

  const handleSelectYear = (e) => {
    setSelectYear(() => Number(e.target.value));
  };

  const handleSelectMonth = (e) => {
    setSelectMonth(() => Number(e.target.value));
  };

  const handleDateManagement = () => {};

  return (
    <>
      <Container>
        <br />
        <br />
        {isLogin && loginState ? (
          <>
            <span style={{ fontSize: "25px" }}>
              {" "}
              {loginState} 님의 하루관리
            </span>
          </>
        ) : (
          <>
            <span style={{ fontSize: "25px" }}>하루관리</span>
          </>
        )}{" "}
        &nbsp;&nbsp;{" "}
        <Image
          src="/img/calendar.png"
          style={{ width: "30px", height: "30px" }}
          rounded
        />{" "}
        &nbsp;오늘은 &nbsp;
        {currentDate} &nbsp;&nbsp;
        {weatherVisible &&
          weatherResult.map((w, i) => (
            <>
              <span key={i}> {switchFn(w.category, w.obsrValue)}</span>;
            </>
          ))}
        ({weatherStandard}기준)
      </Container>

      <hr />
      <br />

      <Container>
        <Form.Select
          style={{ width: "200px", display: "inline-block" }}
          value={selectYear}
          onChange={(e) => handleSelectYear(e)}
        >
          <option value="2025">2025</option>
          <option value="2026">2026</option>
        </Form.Select>
        <Form.Select
          style={{ width: "200px", display: "inline-block" }}
          value={selectMonth}
          onChange={(e) => handleSelectMonth(e)}
        >
          <option value="1">1</option>
          <option value="2">2</option>
          <option value="3">3</option>
          <option value="4">4</option>
          <option value="5">5</option>
          <option value="6">6</option>
          <option value="7">7</option>
          <option value="8">8</option>
          <option value="9">9</option>
          <option value="10">10</option>
          <option value="11">11</option>
          <option value="12">12</option>
        </Form.Select>
        &nbsp;&nbsp;*날짜클릭시 작성한 하루관리를 한눈에 볼수있습니다!
        <br />
        <br />
        {getDate().map((d, i) => (
          <span key="i" style={{ fontSize: "20px" }}>
            <Button
              variant="outline-secondary"
              onClick={() => handleDateManagement()}
            >
              {d}
            </Button>
          </span>
        ))}
        <br />
        <br />
      </Container>

      <Container style={{ textAlign: "center" }}>
        <Card style={{ width: "18rem", display: "inline-block" }}>
          <Card.Img
            variant="top"
            src="/img/oh.png"
            style={{
              display: "block",
              margin: "0 auto",
              width: "200px",
              height: "200px",
              objectFit: "contain",
            }}
          />
          <Card.Body>
            <Card.Title>사진다이어리</Card.Title>
            <Card.Text>내용</Card.Text>
          </Card.Body>
          <ListGroup className="list-group-flush">
            <ListGroup.Item>내용</ListGroup.Item>
            <ListGroup.Item>내용</ListGroup.Item>
            <ListGroup.Item>내용</ListGroup.Item>
          </ListGroup>
          <Card.Body>
            <Card.Link href="#">수정/삭제</Card.Link>
            <Card.Link href="#">다이어리이동</Card.Link>
          </Card.Body>
        </Card>
        &nbsp; &nbsp;
        <Card style={{ width: "18rem", display: "inline-block" }}>
          <Card.Img
            variant="top"
            src="/smile.png"
            style={{
              display: "block",
              margin: "0 auto",
              width: "200px",
              height: "200px",
              objectFit: "contain",
            }}
          />
          <Card.Body>
            <Card.Title>식단</Card.Title>
            <Card.Text>식단</Card.Text>
          </Card.Body>
          <ListGroup className="list-group-flush">
            <ListGroup.Item>아침</ListGroup.Item>
            <ListGroup.Item>점심</ListGroup.Item>
            <ListGroup.Item>저녁</ListGroup.Item>
          </ListGroup>
          <Card.Body>
            <Card.Link href="#">수정/삭제</Card.Link>
            <Card.Link href="#">식단관리이동</Card.Link>
          </Card.Body>
        </Card>
        &nbsp; &nbsp;
        <Card style={{ width: "18rem", display: "inline-block" }}>
          <Card.Img
            variant="top"
            src="/img/sunglass.png"
            style={{
              display: "block",
              margin: "0 auto",
              width: "200px",
              height: "200px",
              objectFit: "contain",
            }}
          />
          <Card.Body>
            <Card.Title>지출</Card.Title>
            <Card.Text>지출</Card.Text>
          </Card.Body>
          <ListGroup className="list-group-flush">
            <ListGroup.Item>수입</ListGroup.Item>
            <ListGroup.Item>지출</ListGroup.Item>
            <ListGroup.Item>이달합계</ListGroup.Item>
          </ListGroup>
          <Card.Body>
            <Card.Link href="#">수정/삭제</Card.Link>
            <Card.Link href="#">수입지출이동</Card.Link>
          </Card.Body>
        </Card>
        &nbsp; &nbsp;
        <Card style={{ width: "18rem", display: "inline-block" }}>
          <Card.Img
            variant="top"
            src="/img/sunglass.png"
            style={{
              display: "block",
              margin: "0 auto",
              width: "200px",
              height: "200px",
              objectFit: "contain",
            }}
          />
          <Card.Body>
            <Card.Title>운동</Card.Title>
            <Card.Text>운동</Card.Text>
          </Card.Body>
          <ListGroup className="list-group-flush">
            <ListGroup.Item>운동내용</ListGroup.Item>
          </ListGroup>
          <Card.Body>
            <Card.Link href="#">수정/삭제</Card.Link>
            <Card.Link href="#">운동이동</Card.Link>
          </Card.Body>
        </Card>
        <br />
      </Container>
      <br />
      <br />
      <br />
    </>
  );
};

export default MainComponent;
