import axios from "axios";
import { dfs_xy_conv } from "../utils/xy";

//const URL = "http://apis.data.go.kr/B551182/";
//http://apis.data.go.kr/1360000/VilageFcstInfoService_2.0
const URL =
  "http://apis.data.go.kr/1360000/VilageFcstInfoService_2.0/getUltraSrtNcst";
//http://apis.data.go.kr/1360000/VilageFcstInfoService_2.0/getUltraSrtNcst
//?serviceKey=인증키&numOfRows=10&pageNo=1
//&base_date=20210628&base_time=0600&nx=55&ny=127

const apiKey = process.env.REACT_APP_API_KEY;
console.log(apiKey);

export const weatherapi = async () => {
  var weatherapiresult = null;
  if (navigator.geolocation) {
    navigator.geolocation.getCurrentPosition(function (pos) {
      console.log(pos);
      var latitude = Math.floor(pos.coords.latitude);
      var longitude = Math.floor(pos.coords.longitude);
      console.log("현재 위치는 : " + latitude + ", " + longitude);

      if (latitude !== null && longitude !== null) {
        const xyresult = dfs_xy_conv("toXY", latitude, longitude);

        console.log("위경도를 xy변경후 : ", xyresult);
        const aaAxios = async () => {
          await axios
            .get(URL, {
              params: {
                serviceKey: apiKey,
                numOfRows: 10,
                pageNo: 1,
                dataType: "JSON",
                base_date: "20250310",
                base_time: "0600",
                nx: xyresult.x,
                ny: xyresult.y,
              },
            })
            .then((result) => {
              console.log("openAPI axios 결과", result);
              weatherapiresult = result.data.response.body.items.item;
              console.log("openAPI axios weatherapiresult", weatherapiresult);
            })
            .catch((e) => {
              console.log("openAPI axios 에러발생", e);
            });
        }; //aaAxios
        aaAxios();
      } //if
    });
  } //geo존재할경우

  return weatherapiresult;
}; //weatherapi
