import React, { useCallback, useEffect, useState } from "react";
import { Link, Navigate, useNavigate, useSearchParams } from "react-router-dom";
import "../layout/stylesCalendar.css";
import Image from "react-bootstrap/Image";
import useCustomMove from "../../hooks/useCustomMove";
import Card from "react-bootstrap/Card";
import useCustomDiet from "../../hooks/useCustomDiet";
import useCustomLogin from "../../hooks/useCustomLogin";
import { getDietList } from "../../api/dietApi";

const chooseReturn = (number) => {
  console.log("DietListComponent.js chooseReturn() ÏßÑÏûÖ Îß§Í∞úÎ≥ÄÏàò => ", number);
  let result = "";
  for (let n of number) {
    switch (n) {
      case "1":
        return (result = "ü•¶ÏïÑÏπ®");
      case "2":
        return (result = "üçÑÏ†êÏã¨");
      case "3":
        return (result = "üßÖÏ†ÄÎÖÅ");
      case "4":
        return (result = "üßÑÍ∞ÑÏãù");
      default:
        return null;
    }
  }
  console.log(result);
};

const makeCalendar = (d) => {
  let date = new Date(d);
  const currentYear = new Date(date).getFullYear(); //Ïòà)Mon Apr 01 2024 10:27:04 GMT+0900 (ÌïúÍµ≠ ÌëúÏ§ÄÏãú)
  const currentMonth = new Date(date).getMonth() + 1;
  const firstDay = new Date(date.setDate(1)).getDay(); //1 ÏïûÏóê0ÏïàÎ∂ôÎäîÎã§

  //ÌòÑÏû¨ Îã¨Î†•Ï≤´Ïß∏ÎÇ†
  let calfirst = "";
  function getCalfirst() {
    calfirst += `${currentYear}-`;

    if (currentMonth < 10) {
      calfirst += `0${currentMonth}`;
    } else {
      calfirst += `${currentMonth}`;
    }
    //calfirst += `01`
    return calfirst;
  } //calfirst ÌòÑÏû¨Îã¨Î†•Ï≤´ÎÇ†

  const lastDay = new Date(currentYear, currentMonth, 0).getDate();
  //console.log("lastDayÍ∞í ->"+lastDay)//30
  const limitDay = firstDay + lastDay;
  //console.log("limitDayÍ∞í ->"+limitDay)//31
  const nextDay = Math.ceil(limitDay / 7) * 7;
  //console.log("nextDayÍ∞í ->"+nextDay)//35 35-31=4Ïπ∏ ÎπàÏπ∏ÎßåÎì§Ïñ¥ÏßÑÎã§.

  return { firstDay, lastDay, limitDay, nextDay };
};

const CalendarItem = (props) => {
  //UserId, content ,createdAt ,id , picture
  const { diet, dateobject } = props.i; //apiÏÑúÎ≤ÑÌõÑ dbÏóêÏÑú Î∞õÏùÄÍ∞í
  console.log("DietListComponent.js props diet=> ", diet);
  console.log("DietListComponent.js props dateobject=> ", dateobject);

  // console.log("props.i.diet[0] ", props.i.diet[0]);
  //console.log("props.i ", props.i);
  return diet.map((i) => (
    <React.Fragment key={i.id}>
      {i.id ? (
        i.choose ? (
          <>
            <br />
            <Link
              to={`../read/${i.choose}/${dateobject}`}
              style={{ textDecoration: "none", color: "black" }}
            >
              {chooseReturn(i.choose)}
              {/* <Image
                src={`http://localhost:8001/img/${i.picture}`}
                rounded
                variant="top"
                style={{
                  display: "block",
                  margin: "0 auto",
                  maxWidth: "50px",
                  maxHeight: "50px",
                  objectFit: "contain",
                }}
              /> */}
            </Link>
          </>
        ) : (
          <></> //ÏóÜÎäîÍ≤ΩÏö∞
        )
      ) : (
        <></> //ÏóÜÎäîÍ≤ΩÏö∞
      )}
    </React.Fragment>
  ));
};

const DietListComponent = () => {
  const [searchParams] = useSearchParams();
  const { moveToCreate } = useCustomMove();
  const { effectException } = useCustomLogin();
  const navigate = useNavigate();

  const [currentDate, setCurrentDate] = useState("");
  const [diet, setDiet] = useState([]);

  let searchYear = searchParams.get("year");
  searchYear = Number(searchYear);
  let searchMonth = searchParams.get("month");
  searchMonth = Number(searchMonth);

  useEffect(() => {
    getDietList()
      .then((result) => {
        console.log("DietListComponet.js then()ÏßÑÏûÖ ", result);
        setDiet(result);
        let tempdate = new Date();
        let currentYear = new Date(tempdate).getFullYear();
        let currentMonth = new Date(tempdate).getMonth() + 1;
        currentMonth = currentMonth < 10 ? "0" + currentMonth : currentMonth;
        let dateobject = `${currentYear}-${currentMonth}-01`;
        setCurrentDate(() => dateobject);
      })
      .catch((err) => {
        console.log("DietListComponet.js catch()ÏßÑÏûÖ ", err);
        effectException(err);
        //navigate("/");
      });
    return () => {
      setCurrentDate(() => "");
    };
  }, []);

  // let date = searchParams.get("yearmonth")
  //   ? searchParams.get("yearmonth")
  //   : props.today;

  // let date = props.today;

  let date = new Date();

  if (searchMonth && searchYear) {
    date = new Date().setFullYear(searchYear).setMonth(searchMonth - 1);
  }

  const { firstDay, nextDay, limitDay, lastDay } = makeCalendar(date);

  // console.log("date -> ", date);
  // console.log("firstDay -> ", firstDay);
  // console.log("nextDay -> ", nextDay);
  // console.log("limitDay -> ", limitDay);
  // console.log("lastDay -> ", lastDay);

  let dateItem = [];

  for (let i = 1, j = 0; i <= lastDay; i++, j++) {
    let currentYear = new Date(date).getFullYear();
    let currentMonth = new Date(date).getMonth() + 1;
    currentMonth = currentMonth < 10 ? "0" + currentMonth : currentMonth;
    let ichange = i < 10 ? "0" + i : i;
    let dateobject = `${currentYear}-${currentMonth}-${ichange}`;

    dateItem[j] = {
      dateitem: i,
      dateobject: dateobject,
      diet: diet.filter((a) => a.dateobject === dateobject),
    };
  }
  console.log("dietÎÇ¥Ïö©Ï∂úÎ†• => ", dateItem[0].diet, "ÎëêÎ≤àÏß∏", dateItem[1].diet);

  //Îã¨Î†•ÏóêÏÑú Ïù¥Ï†ÑÎπàÏπ∏
  const beforeDateItem = firstDay - 0;
  let beforeDateItemArray = [];

  if (beforeDateItem !== 0) {
    for (let i = 1, j = 0; i <= beforeDateItem; i++, j++) {
      beforeDateItemArray[j] = i;
    }
  }
  // console.log("beforeDateItem -> ", beforeDateItem);

  //Îã¨Î†•ÏóêÏÑú Ïù¥ÌõÑÎπàÏπ∏
  const afterDateItem = nextDay - limitDay;
  let afterDateItemArray = [];

  if (afterDateItem !== 0) {
    for (let i = 1, j = 0; i <= afterDateItem; i++, j++) {
      afterDateItemArray[j] = i;
    }
  }

  // console.log("afterDateItem -> ", afterDateItem);

  // const [dateItem, setDateItem] = useState([1, ...lastDay]);
  // const [beforeDateItem, setBeforeDateItem] = useState(firstDay - 0);
  // const [afterDateItem, setAfterDateItem] = useState(nextDay - limitDay);

  // Ïù¥Ï†ÑÎã¨ Ïù¥Îèô
  const BeforeMonthMove = useCallback(
    () => {
      console.log("BeforeMonthMove() ÏßÑÏûÖ  => ", currentDate);
      const tempdate = new Date(currentDate);
      let beforechangeDate = new Date(
        tempdate.setMonth(tempdate.getMonth() - 1)
      );
      let beforecurrentYear = new Date(beforechangeDate).getFullYear();
      let beforechangeMonth = new Date(beforechangeDate).getMonth() + 1;
      beforechangeMonth =
