import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Card from "react-bootstrap/Card";
import ListGroup from "react-bootstrap/ListGroup";
import useCustomLogin from "../../hooks/useCustomLogin";
import { useEffect, useState } from "react";
import { weatherapi } from "../../api/openApi";
import Form from "react-bootstrap/Form";

const switchFn = (category, obsrValue) => {
  let result = "";
  console.log("T1H 진입");

  switch (category) {
    case "T1H":
      result += `기온 : ${obsrValue}`;
      break;
    case "PTY":
      result += `강수 : ${obsrValue}`;
      break;
    case "REH":
      result += `습도 : ${obsrValue}`;
      break;
    case "RN1":
      result += `강수량 : ${obsrValue}`;
      break;
    case "WSD":
      result += `풍속 : ${obsrValue}`;
      break;
    default:
      break;
  }
  console.log("switchFn 결과 -> ", result);
  return result;
};

const MainComponent = () => {
  const { loginState, isLogin } = useCustomLogin();
  console.log(isLogin);

  console.log(loginState); //user, accessToken 2개 들어있다 이렇게 슬라이스에보관

  //공공api
  const [weatherVisible, setWeatherVisible] = useState(false);
  const [weatherResult, setWeatherResult] = useState([]);
  const [currentDate, setCurrentDate] = useState("");
  const [weatherTime, setWeatherTime] = useState("");
  const [weatherDate, setWeatherDate] = useState("");
  const [weatherStandard, setWeatherStandard] = useState("");
  const [selectMonth, setSelectMonth] = useState("");
  const [selectYear, setSelectYear] = useState("");
  //
  //  base_date: "20250310",
  // base_time: "2200",

  useEffect(() => {}, []);

  useEffect(() => {
    const currentDateFn = () => {
      console.log("MainComponent.js weatherapiEI 내 currentDateFn() 진입");

      const today = new Date();
      // 현재 날짜를 가져옵니다.

      let hour =
        today.getHours() < 10 ? "0" + today.getHours() : today.getHours();
      let minutes =
        today.getMinutes() < 10 ? "0" + today.getMinutes() : today.getMinutes();
      minutes = minutes < 30 ? "00" : "30";
      setWeatherTime(() => hour + minutes);
      setWeatherStandard(() => `${hour}시 ${minutes}분`);
      setCurrentDate(
        () =>
          `${today.getFullYear()}년 ${
            today.getMonth() + 1
          }월 ${today.getDate()}일`
      );

      let monthfinal =
        today.getMonth() + 1 < 10
          ? "0" + today.getMonth() + 1
          : today.getMonth() + 1;
      let datefinal =
        today.getDate() < 10 ? "0" + today.getDate() : today.getDate();
      setWeatherDate(() => `${today.getFullYear()}${monthfinal}${datefinal}`);
    };
    currentDateFn();
    const weatherapiEI = async () => {
      console.log("MainComponent.js weatherapiEI 내 weatherapi() 진입");
      const result = await weatherapi(weatherDate, weatherTime);
      if (Array.isArray(result)) {
        console.log("BasicMenu.js useEffect() 내 공공API 결과확인 ", result); //배열로받음
        let newObj = [];
        result.forEach((r) => {
          let newO = {};
          newO["category"] = r.category;
          newO["obsrValue"] = r.obsrValue;
          return newObj.push(newO);
        });
        console.log("BasicMenu.js useEffect() 배열로 category만 남김 ", newObj); //배열로 category만 남김

        setWeatherResult(() => {
          return newObj;
        });
        setWeatherVisible(true);
        console.log(
          "BasicMenu.js useEffect() weatehrReuslt => ",
          weatherResult
        );
      }
    };
    weatherapiEI();
  }, []);

  const getDate = (param) => {
    let getDateResult = null;
    let date = new Date(param);
    let firstDay = new Date(date.getFullYear(), date.getMonth(), 1);

    let lastDay = new Date(date.getFullYear(), date.getMonth() + 1, 0);

    for (let i = firstDay; i <= lastDay; i++) {
      getDateResult += " " + i;
    }
    return getDateResult;
  };

  const handleSelectDate = (e) => {
    console.log("handleSelectDate() 진입");
    if (e.target.name === "year") {
      setSelectYear(e.target.year.value);
    }

    if (e.target.name === "month") {
      setSelectYear(e.target.month.value);
    }
  };

  return (
    <>
      <Container>
        <br />
        <br />
        {isLogin && loginState ? (
          <>
            <h4> {loginState} 님의 하루관리</h4>
          </>
        ) : (
          <>
            <h4>하루관리</h4>
          </>
        )}
        {currentDate}
        {weatherVisible &&
          weatherResult.map((w, i) => (
            <>
              <span key={i}> {switchFn(w.category, w.obsrValue)}</span>;
            </>
          ))}
        ({weatherStandard}기준)
        <hr />
        <br />
      </Container>
      <br />
      <br />

      <Container>
        <Form.Select style={{ width: "200px", display: "inline-block" }}>
          <option name="year" onChange={(e) => handleSelectDate(e)}>
            년도
          </option>
          <option value="1" selected>
            2025
          </option>
          <option value="2">2026</option>
        </Form.Select>
        <Form.Select style={{ width: "200px" }}>
          <option name="month" onChange={(e) => handleSelectDate(e)}>
            월
          </option>
          <option value="1" selected>
            1
          </option>
          <option value="2">2</option>
          <option value="3">3</option>
          <option value="4">4</option>
          <option value="5">5</option>
          <option value="6">6</option>
          <option value="7">7</option>
          <option value="8">8</option>
          <option value="9">9</option>
          <option value="10">10</option>
          <option value="11">11</option>
          <option value="12">12</option>
        </Form.Select>
        날짜클릭시 한눈에 작성한 하루관리를 볼수있습니다!
        <br />
        {selectYear !== "" && selectMonth !== "" && getDate()}
        <br />
      </Container>

      <Container style={{ textAlign: "center" }}>
        <Card style={{ width: "18rem", display: "inline-block" }}>
          <Card.Img
            variant="top"
            src="/img/oh.png"
            style={{
              display: "block",
              margin: "0 auto",
              width: "200px",
              height: "200px",
              objectFit: "contain",
            }}
          />
          <Card.Body>
            <Card.Title>사진다이어리</Card.Title>
            <Card.Text>내용</Card.Text>
          </Card.Body>
          <ListGroup className="list-group-flush">
            <ListGroup.Item>내용</ListGroup.Item>
            <ListGroup.Item>내용</ListGroup.Item>
            <ListGroup.Item>내용</ListGroup.Item>
          </ListGroup>
          <Card.Body>
            <Card.Link href="#">수정/삭제</Card.Link>
            <Card.Link href="#">다이어리이동</Card.Link>
          </Card.Body>
        </Card>
        &nbsp; &nbsp;
        <Card style={{ width: "18rem", display: "inline-block" }}>
          <Card.Img
            variant="top"
            src="/smile.png"
            style={{
              display: "block",
              margin: "0 auto",
              width: "200px",
              height: "200px",
              objectFit: "contain",
            }}
          />
          <Card.Body>
            <Card.Title>식단</Card.Title>
            <Card.Text>식단</Card.Text>
          </Card.Body>
          <ListGroup className="list-group-flush">
            <ListGroup.Item>아침</ListGroup.Item>
            <ListGroup.Item>점심</ListGroup.Item>
            <ListGroup.Item>저녁</ListGroup.Item>
          </ListGroup>
          <Card.Body>
            <Card.Link href="#">수정/삭제</Card.Link>
            <Card.Link href="#">식단관리이동</Card.Link>
          </Card.Body>
        </Card>
        &nbsp; &nbsp;
        <Card style={{ width: "18rem", display: "inline-block" }}>
          <Card.Img
            variant="top"
            src="/img/sunglass.png"
            style={{
              display: "block",
              margin: "0 auto",
              width: "200px",
              height: "200px",
              objectFit: "contain",
            }}
          />
          <Card.Body>
            <Card.Title>지출</Card.Title>
            <Card.Text>지출</Card.Text>
          </Card.Body>
          <ListGroup className="list-group-flush">
            <ListGroup.Item>수입</ListGroup.Item>
            <ListGroup.Item>지출</ListGroup.Item>
            <ListGroup.Item>이달합계</ListGroup.Item>
          </ListGroup>
          <Card.Body>
            <Card.Link href="#">수정/삭제</Card.Link>
            <Card.Link href="#">수입지출이동</Card.Link>
          </Card.Body>
        </Card>
        &nbsp; &nbsp;
        <Card style={{ width: "18rem", display: "inline-block" }}>
          <Card.Img
            variant="top"
            src="/img/sunglass.png"
            style={{
              display: "block",
              margin: "0 auto",
              width: "200px",
              height: "200px",
              objectFit: "contain",
            }}
          />
          <Card.Body>
            <Card.Title>운동</Card.Title>
            <Card.Text>운동</Card.Text>
          </Card.Body>
          <ListGroup className="list-group-flush">
            <ListGroup.Item>운동내용</ListGroup.Item>
          </ListGroup>
          <Card.Body>
            <Card.Link href="#">수정/삭제</Card.Link>
            <Card.Link href="#">운동이동</Card.Link>
          </Card.Body>
        </Card>
        <br />
      </Container>
      <br />
      <br />
      <br />
    </>
  );
};

export default MainComponent;
