import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import { useSelector } from "react-redux";

const BasicMenu = (props) => {
  //툴킷 useSelector
  //  const loginState = useSelector((state) => state.loginSlice);
  // console.log(
  //   "BasicMenu.js 에서 useSelector() 로 loginSlice 상태 email => ",
  //   loginState.email
  // );
  return (
    <>
      <Navbar bg="light" data-bs-theme="light">
        <Container>
          <Navbar.Brand href="/">
            <img src="/img/how.png" height={"50px"} width={"50px"} />
            하루관리
          </Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link href="/">Home</Nav.Link>
            {props.isLogin ? (
              <>
                <Nav.Link href="/diary">사진일기</Nav.Link>
                <Nav.Link href="/diet">식단관리</Nav.Link>
                <Nav.Link href="/exercise">운동관리</Nav.Link>
                <Nav.Link href="/money">지출관리</Nav.Link>
                <Nav.Link href="/logout">로그아웃</Nav.Link>
              </>
            ) : (
              <>
                <Nav.Link href="/login">로그인</Nav.Link>
                <Nav.Link href="/join">회원가입</Nav.Link>
              </>
            )}{" "}
            <Nav.Link href="/chat">채팅초대</Nav.Link>
          </Nav>
        </Container>
      </Navbar>
    </>
  );
};

export default BasicMenu;

